import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertGameSchema, insertPlayerSchema, insertPlayerAnswerSchema, insertQuestionSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {

  // Create a new game with questions
  app.post("/api/games", async (req, res) => {
    try {
      const requestSchema = z.object({
        name: z.string().min(1),
        questions: z.array(z.object({
          text: z.string().min(1),
          options: z.array(z.string()).min(2).max(6),
          correctAnswer: z.number().min(0)
        })).min(1).max(50)
      });

      const { name, questions } = requestSchema.parse(req.body);

      // Transform questions to match storage interface
      const questionsForStorage = questions.map((q, index) => ({
        text: q.text,
        options: q.options,
        correctAnswer: q.correctAnswer,
        gameId: "", // Will be set by storage
        order: index + 1
      }));

      const game = await storage.createGame({ name }, questionsForStorage);

      res.json(game);
    } catch (error) {
      res.status(400).json({ message: error instanceof Error ? error.message : "Invalid game data" });
    }
  });

  // Get game by ID
  app.get("/api/games/:gameId", async (req, res) => {
    try {
      const { gameId } = req.params;
      const game = await storage.getGame(gameId);

      if (!game) {
        return res.status(404).json({ message: "Game not found" });
      }

      res.json(game);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Get game by code
  app.get("/api/games/code/:code", async (req, res) => {
    try {
      const { code } = req.params;
      const game = await storage.getGameByCode(code.toUpperCase());

      if (!game) {
        return res.status(404).json({ message: "Game not found" });
      }

      res.json(game);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Get game questions
  app.get("/api/games/:gameId/questions", async (req, res) => {
    try {
      const { gameId } = req.params;
      const questions = await storage.getGameQuestions(gameId);
      res.json(questions);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Create a player
  app.post("/api/players", async (req, res) => {
    try {
      const playerData = insertPlayerSchema.parse(req.body);

      // Check if player already exists for this game
      const existingPlayer = await storage.findExistingPlayer(
        playerData.name,
        playerData.phone,
        playerData.gameId
      );

      if (existingPlayer) {
        // Player already exists - return existing player info
        res.json({ 
          ...existingPlayer, 
          isExisting: true,
          hasCompleted: !!existingPlayer.completedAt 
        });
        return;
      }

      // Create new player if doesn't exist
      const player = await storage.createPlayer(playerData);
      res.json({ ...player, isExisting: false, hasCompleted: false });
    } catch (error) {
      res.status(400).json({ message: error instanceof Error ? error.message : "Invalid player data" });
    }
  });

  // Submit player answer
  app.post("/api/players/:playerId/answers", async (req, res) => {
    try {
      const { playerId } = req.params;
      const answerData = insertPlayerAnswerSchema.parse({
        ...req.body,
        playerId
      });

      const answer = await storage.createPlayerAnswer(answerData);
      res.json(answer);
    } catch (error) {
      res.status(400).json({ message: error instanceof Error ? error.message : "Invalid answer data" });
    }
  });

  // Complete game for player
  app.post("/api/players/:playerId/complete", async (req, res) => {
    try {
      const { playerId } = req.params;

      // Get all player answers to calculate final score
      const answers = await storage.getPlayerAnswers(playerId);
      const totalScore = answers.reduce((sum, answer) => sum + answer.points, 0);
      const correctAnswers = answers.filter(answer => answer.isCorrect).length;
      const totalAnswers = answers.length;
      const averageTime = Math.round(answers.reduce((sum, answer) => sum + answer.timeSpent, 0) / answers.length);

      await storage.updatePlayerScore(playerId, totalScore, correctAnswers, totalAnswers, averageTime);
      await storage.completePlayer(playerId);

      const player = await storage.getPlayer(playerId);
      res.json(player);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Get game leaderboard
  app.get("/api/games/:gameId/leaderboard", async (req, res) => {
    try {
      const { gameId } = req.params;
      const players = await storage.getPlayersByGame(gameId);
      res.json(players);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Get today's top players (across all games)
  app.get("/api/leaderboard/today", async (req, res) => {
    try {
      // For now, return mock data since we don't have date filtering
      // In a real implementation, you'd filter by today's date
      const mockLeaderboard = [
        { name: "أحمد محمد", score: 2580 },
        { name: "سارة علي", score: 2340 },
        { name: "محمد خالد", score: 2180 }
      ];
      res.json(mockLeaderboard);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Get today's game statistics
  app.get("/api/stats/today", async (req, res) => {
    try {
      // Count games and players created today
      const allGames = await storage.getAllGames();
      const allPlayers = await storage.getAllPlayers();

      const today = new Date();
      today.setHours(0, 0, 0, 0);

      const gamesPlayedToday = allGames.filter(game => {
        const gameDate = new Date(game.createdAt);
        gameDate.setHours(0, 0, 0, 0);
        return gameDate.getTime() === today.getTime();
      }).length;

      const totalPlayers = allPlayers.length;

      res.json({
        gamesPlayedToday,
        totalPlayers
      });
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Admin endpoint - only for you to see analytics with phone numbers
  // Removed admin analytics endpoint for security - exposed PII without authentication

  // Track visitor
  app.post("/api/visitors/track", async (req, res) => {
    try {
      const newCount = await storage.incrementVisitors();
      res.json({ count: newCount });
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Get visitor count
  app.get("/api/visitors/count", async (req, res) => {
    try {
      const count = await storage.getVisitorCount();
      res.json({ count });
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type Game, type Player, type Question, type PlayerAnswer, type SiteStats, type InsertGame, type InsertPlayer, type InsertQuestion, type InsertPlayerAnswer, siteStats, games, players, questions, playerAnswers } from "@shared/schema";
import { randomUUID } from "crypto";
import { drizzle } from "drizzle-orm/neon-http";
import { neon } from "@neondatabase/serverless";
import { eq, sql, desc, lt, and } from "drizzle-orm";

export interface IStorage {
  // Games
  createGame(game: InsertGame, questions: InsertQuestion[]): Promise<Game>;
  getGameByCode(code: string): Promise<Game | undefined>;
  getGame(id: string): Promise<Game | undefined>;
  getAllGames(): Promise<Game[]>;

  // Players
  findExistingPlayer(name: string, phone: string, gameId: string): Promise<Player | undefined>;
  createPlayer(player: InsertPlayer): Promise<Player>;
  getPlayer(id: string): Promise<Player | undefined>;
  getPlayersByGame(gameId: string): Promise<Player[]>;
  getAllPlayers(): Promise<Player[]>;
  getWinnersCount(): Promise<number>;
  updatePlayerScore(playerId: string, score: number, correctAnswers: number, totalAnswers: number, averageTime: number): Promise<void>;
  completePlayer(playerId: string): Promise<void>;

  // Questions
  getGameQuestions(gameId: string): Promise<Question[]>;
  getQuestion(id: string): Promise<Question | undefined>;

  // Player Answers
  createPlayerAnswer(answer: InsertPlayerAnswer): Promise<PlayerAnswer>;
  getPlayerAnswers(playerId: string): Promise<PlayerAnswer[]>;

  // Site Stats
  incrementVisitors(): Promise<number>;
  getVisitorCount(): Promise<number>;
}

// Database connection
const dbUrl = process.env.DATABASE_URL;
if (!dbUrl) {
  throw new Error("DATABASE_URL is not set");
}
const sql_client = neon(dbUrl);
const db = drizzle(sql_client);

export class DatabaseStorage implements IStorage {
  constructor() {
    // Initialize cleanup job that runs every hour
    this.startCleanupJob();
  }

  private generateGameCode(): string {
    const chars = 'ABCDEFGHJKLMNPQRSTUVWXYZ23456789';
    let result = '';
    for (let i = 0; i < 6; i++) {
      result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return result;
  }

  private async generateUniqueGameCode(): Promise<string> {
    let code: string;
    let attempts = 0;
    const maxAttempts = 10;

    do {
      code = this.generateGameCode();
      const existingGame = await this.getGameByCode(code);
      if (!existingGame) {
        return code;
      }
      attempts++;
    } while (attempts < maxAttempts);

    // If we can't generate a unique code after 10 attempts, add timestamp
    return this.generateGameCode() + Date.now().toString().slice(-2);
  }

  private startCleanupJob(): void {
    // Run cleanup every hour
    setInterval(async () => {
      try {
        await this.cleanupOldGames();
      } catch (error) {
        console.error('Cleanup job error:', error);
      }
    }, 60 * 60 * 1000); // 1 hour in milliseconds

    // Run initial cleanup
    this.cleanupOldGames().catch(console.error);
  }

  private async cleanupOldGames(): Promise<void> {
    const fortyEightHoursAgo = new Date(Date.now() - 48 * 60 * 60 * 1000);

    try {
      // Get games older than 48 hours
      const oldGames = await db
        .select({ id: games.id })
        .from(games)
        .where(lt(games.createdAt, fortyEightHoursAgo));

      if (oldGames.length > 0) {
        const gameIds = oldGames.map(game => game.id);

        // Delete in correct order due to foreign key constraints
        // 1. Delete player answers first
        for (const gameId of gameIds) {
          await db.delete(playerAnswers)
            .where(sql`${playerAnswers.playerId} IN (SELECT id FROM ${players} WHERE ${players.gameId} = ${gameId})`);
        }

        // 2. Delete players
        for (const gameId of gameIds) {
          await db.delete(players)
            .where(eq(players.gameId, gameId));
        }

        // 3. Delete questions
        for (const gameId of gameIds) {
          await db.delete(questions)
            .where(eq(questions.gameId, gameId));
        }

        // 4. Finally delete games
        for (const gameId of gameIds) {
          await db.delete(games)
            .where(eq(games.id, gameId));
        }

        console.log(`Cleaned up ${oldGames.length} games older than 48 hours`);
      }
    } catch (error) {
      console.error('Error during cleanup:', error);
    }
  }

  async createGame(insertGame: InsertGame, questionsList: InsertQuestion[]): Promise<Game> {
    const code = await this.generateUniqueGameCode();

    // Insert game
    const [game] = await db
      .insert(games)
      .values({
        ...insertGame,
        code,
      })
      .returning();

    // Insert questions for this game
    if (questionsList.length > 0) {
      const questionsToInsert = questionsList.map((questionData, index) => ({
        ...questionData,
        gameId: game.id,
        order: index + 1,
      }));

      await db.insert(questions).values(questionsToInsert);
    }

    return game;
  }

  async getGameByCode(code: string): Promise<Game | undefined> {
    const result = await db
      .select()
      .from(games)
      .where(eq(games.code, code))
      .limit(1);

    return result[0];
  }

  async getGame(id: string): Promise<Game | undefined> {
    const result = await db
      .select()
      .from(games)
      .where(eq(games.id, id))
      .limit(1);

    return result[0];
  }

  async getAllGames(): Promise<Game[]> {
    return await db.select().from(games).orderBy(desc(games.createdAt));
  }

  async findExistingPlayer(name: string, phone: string, gameId: string): Promise<Player | undefined> {
    const result = await db
      .select()
      .from(players)
      .where(and(
        eq(players.name, name),
        eq(players.phone, phone),
        eq(players.gameId, gameId)
      ))
      .limit(1);

    return result[0];
  }

  async createPlayer(insertPlayer: InsertPlayer): Promise<Player> {
    const [player] = await db
      .insert(players)
      .values(insertPlayer)
      .returning();

    return player;
  }

  async getPlayer(id: string): Promise<Player | undefined> {
    const result = await db
      .select()
      .from(players)
      .where(eq(players.id, id))
      .limit(1);

    return result[0];
  }

  async getPlayersByGame(gameId: string): Promise<Player[]> {
    return await db
      .select()
      .from(players)
      .where(eq(players.gameId, gameId))
      .orderBy(desc(players.score));
  }

  async getAllPlayers(): Promise<Player[]> {
    return await db.select().from(players);
  }

  async getWinnersCount(): Promise<number> {
    const allGames = await this.getAllGames();
    let winnersCount = 0;

    for (const game of allGames) {
      const gamePlayers = await this.getPlayersByGame(game.id);
      // Count completed players (those who finished the game)
      const completedPlayers = gamePlayers.filter(p => p.completedAt !== null);

      // If there are completed players, the top scorer is the winner
      if (completedPlayers.length > 0) {
        winnersCount += 1; // One winner per game
      }
    }

    return winnersCount;
  }

  async updatePlayerScore(playerId: string, score: number, correctAnswers: number, totalAnswers: number, averageTime: number): Promise<void> {
    await db
      .update(players)
      .set({
        score,
        correctAnswers,
        totalAnswers,
        averageTime,
      })
      .where(eq(players.id, playerId));
  }

  async completePlayer(playerId: string): Promise<void> {
    await db
      .update(players)
      .set({
        completedAt: new Date(),
      })
      .where(eq(players.id, playerId));
  }

  async getGameQuestions(gameId: string): Promise<Question[]> {
    const gameQuestions = await db
      .select()
      .from(questions)
      .where(eq(questions.gameId, gameId))
      .orderBy(questions.order);

    // Randomize question order
    const shuffledQuestions = [...gameQuestions].sort(() => Math.random() - 0.5);

    // Randomize answer options for each question and update correct answer index
    return shuffledQuestions.map(question => {
      const optionsWithIndex = question.options.map((option, index) => ({
        option,
        wasCorrect: index === question.correctAnswer
      }));

      // Shuffle the options
      const shuffledOptions = [...optionsWithIndex].sort(() => Math.random() - 0.5);

      // Find the new index of the correct answer
      const newCorrectIndex = shuffledOptions.findIndex(item => item.wasCorrect);

      return {
        ...question,
        options: shuffledOptions.map(item => item.option),
        correctAnswer: newCorrectIndex
      };
    });
  }

  async getQuestion(id: string): Promise<Question | undefined> {
    const result = await db
      .select()
      .from(questions)
      .where(eq(questions.id, id))
      .limit(1);

    return result[0];
  }

  async createPlayerAnswer(insertAnswer: InsertPlayerAnswer): Promise<PlayerAnswer> {
    // Calculate points based on correctness and speed
    let points = 0;
    if (insertAnswer.isCorrect) {
      // Base points for correct answer: 1000
      // Bonus points for speed: max 500 points (decreases with time)
      const basePoints = 1000;
      const speedBonus = Math.max(0, 500 - (insertAnswer.timeSpent * 25));
      points = Math.round(basePoints + speedBonus);
    }

    const [playerAnswer] = await db
      .insert(playerAnswers)
      .values({
        ...insertAnswer,
        points,
      })
      .returning();

    return playerAnswer;
  }

  async getPlayerAnswers(playerId: string): Promise<PlayerAnswer[]> {
    return await db
      .select()
      .from(playerAnswers)
      .where(eq(playerAnswers.playerId, playerId));
  }

  async incrementVisitors(): Promise<number> {
    // Atomic increment with upsert to handle concurrent visitors properly
    const result = await db
      .insert(siteStats)
      .values({ id: 'main', visitors: 1 })
      .onConflictDoUpdate({
        target: siteStats.id,
        set: {
          visitors: sql`${siteStats.visitors} + 1`,
          updatedAt: new Date()
        }
      })
      .returning();

    return result[0].visitors;
  }

  async getVisitorCount(): Promise<number> {
    const stats = await db.select().from(siteStats).where(eq(siteStats.id, 'main'));
    return stats.length > 0 ? stats[0].visitors : 0;
  }
}

export const storage = new DatabaseStorage();

