import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Link, useSearch, useLocation } from "wouter";
import { Copy, Share2, Play, BarChart3 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { playSound } from "@/lib/soundUtils";

interface Game {
  id: string;
  name: string;
  code: string;
  createdAt: string;
  isActive: boolean;
}

export default function GameCreated() {
  const search = useSearch();
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  const params = new URLSearchParams(search);
  const gameId = params.get("gameId");

  const { data: game, isLoading } = useQuery<Game>({
    queryKey: ["/api/games", gameId],
    enabled: !!gameId,
  });

  const copyGameCode = async () => {
    if (game?.code) {
      try {
        await navigator.clipboard.writeText(game.code);
        toast({
          title: "تم النسخ!",
          description: "تم نسخ رمز اللعبة إلى الحافظة",
        });
      } catch (error) {
        toast({
          title: "خطأ",
          description: "لم يتم نسخ الرمز. يرجى النسخ يدوياً.",
          variant: "destructive",
        });
      }
    }
  };

  const shareGame = async () => {
    if (game?.code && navigator.share) {
      try {
        await navigator.share({
          title: `انضم إلى لعبة ${game.name}`,
          text: `استخدم الرمز: ${game.code}`,
          url: window.location.origin,
        });
      } catch (error) {
        copyGameCode();
      }
    } else {
      copyGameCode();
    }
  };

  const startGame = () => {
    setLocation(`/registration?gameId=${gameId}&mode=create`);
  };

  if (isLoading || !game) {
    return (
      <div className="min-h-screen trivia-background relative overflow-hidden flex items-center justify-center">
        <div className="absolute inset-0 bg-background/30"></div>
        <div className="text-center relative z-10">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">جارٍ تحميل اللعبة...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen trivia-background relative overflow-hidden">
      {/* Subtle overlay for content readability */}
      <div className="absolute inset-0 bg-background/30"></div>
      <div className="container mx-auto px-4 py-8 max-w-md relative z-10">
        <div className="text-center mb-8" data-testid="header-game-created">
          <div className="text-6xl mb-4">🎉</div>
          <h2 className="text-3xl font-bold font-arabic text-primary mb-2">
            تم إنشاء اللعبة!
          </h2>
          <p className="text-muted-foreground">Game Created Successfully</p>
        </div>

        {/* Game Info */}
        <Card className="border border-border shadow-lg mb-6">
          <CardContent className="p-6">
            <div className="text-center" data-testid="game-info">
              <h3 className="text-xl font-medium mb-4" data-testid="game-name">
                {game.name}
              </h3>

              <div className="bg-gradient-to-r from-primary/20 to-accent/20 border border-primary/30 rounded-xl p-6 mb-6">
                <p className="text-sm text-muted-foreground mb-2">
                  رمز اللعبة / Game Code
                </p>
                <div className="text-4xl font-bold font-mono text-primary mb-4" data-testid="game-code">
                  {game.code}
                </div>
                <p className="text-sm text-muted-foreground">
                  شارك هذا الرمز مع الأصدقاء للانضمام
                </p>
              </div>

              <div className="flex gap-2">
                <Button
                  onClick={() => {
                    playSound.buttonClick();
                    copyGameCode();
                  }}
                  variant="outline"
                  className="flex-1 bg-card border border-border hover:bg-muted text-card-foreground"
                  data-testid="button-copy-code"
                >
                  <Copy className="w-4 h-4 mr-2" />
                  نسخ الرمز
                </Button>

                <Button
                  onClick={() => {
                    playSound.buttonClick();
                    shareGame();
                  }}
                  variant="outline"
                  className="flex-1 bg-card border border-border hover:bg-muted text-card-foreground"
                  data-testid="button-share-code"
                >
                  <Share2 className="w-4 h-4 mr-2" />
                  مشاركة
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Action Buttons */}
        <div className="space-y-4">
          <Button
            onClick={() => {
              playSound.buttonClick();
              startGame();
            }}
            className="w-full bg-orange-500 hover:bg-orange-600 text-white font-medium py-4 px-6 rounded-xl shadow-lg shadow-orange-500/30 hover:shadow-orange-500/50 transition-all duration-300 transform hover:scale-[1.02]"
            data-testid="button-start-playing"
          >
            <Play className="w-5 h-5 mr-2" />
            ابدأ اللعب الآن / Start Playing Now
          </Button>

          <Link href={`/leaderboard-view?gameId=${gameId}`}>
            <Button
              onClick={playSound.buttonClick}
              variant="outline"
              className="w-full bg-amber-500 hover:bg-amber-600 border border-amber-400 text-white font-medium py-3 px-6 rounded-lg shadow-lg shadow-amber-500/20 hover:shadow-amber-500/40 transition-all duration-300 transform hover:scale-[1.02]"
              data-testid="button-view-leaderboard"
            >
              <BarChart3 className="w-5 h-5 mr-2" />
              عرض النتائج / View Leaderboard
            </Button>
          </Link>

          <Link href="/">
            <Button
              onClick={playSound.buttonClick}
              variant="outline"
              className="w-full bg-card border border-border hover:bg-muted text-card-foreground font-medium py-3 px-6 rounded-lg shadow-lg transition-all duration-300"
              data-testid="button-home"
            >
              العودة للصفحة الرئيسية / Back to Home
            </Button>
          </Link>
        </div>
      </div>
    </div>
  );
}

client/src/pages/game-play.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { useLocation, useSearch } from "wouter";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { playSound } from "@/lib/soundUtils";

interface Question {
  id: string;
  gameId: string;
  text: string;
  options: string[];
  correctAnswer: number;
  order: number;
}

export default function GamePlay() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [timeRemaining, setTimeRemaining] = useState(20);
  const [selectedAnswer, setSelectedAnswer] = useState<number | null>(null);
  const [isAnswered, setIsAnswered] = useState(false);
  const [answers, setAnswers] = useState<Array<{
    questionId: string;
    selectedAnswer: number;
    isCorrect: boolean;
    timeSpent: number;
  }>>([]);

  const [, setLocation] = useLocation();
  const search = useSearch();
  const { toast } = useToast();

  const params = new URLSearchParams(search);
  const playerId = params.get("playerId");
  const gameId = params.get("gameId");

  const { data: questions, isLoading } = useQuery<Question[]>({
    queryKey: ["/api/games", gameId, "questions"],
    enabled: !!gameId,
  });

  const submitAnswerMutation = useMutation({
    mutationFn: async (answerData: {
      questionId: string;
      selectedAnswer: number;
      isCorrect: boolean;
      timeSpent: number;
    }) => {
      const response = await apiRequest("POST", `/api/players/${playerId}/answers`, answerData);
      return response.json();
    },
  });

  const completeGameMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", `/api/players/${playerId}/complete`);
      return response.json();
    },
    onSuccess: () => {
      setLocation(`/game-results?playerId=${playerId}&gameId=${gameId}`);
    },
  });

  // Timer countdown with sound effects
  useEffect(() => {
    if (timeRemaining > 0 && !isAnswered) {
      const timer = setTimeout(() => {
        const newTime = timeRemaining - 1;
        setTimeRemaining(newTime);

        // Play sound effects based on time remaining
        if (newTime <= 5 && newTime > 0) {
          playSound.warningTick(); // Warning sound for last 5 seconds
        } else if (newTime > 5) {
          playSound.countdownTick(); // Regular tick sound
        }

        // Time warning sound at 10 seconds
        if (newTime === 10) {
          playSound.timeWarning();
        }
      }, 1000);
      return () => clearTimeout(timer);
    } else if (timeRemaining === 0 && !isAnswered) {
      // Time's up, auto-submit wrong answer
      handleAnswerSelect(-1);
    }
  }, [timeRemaining, isAnswered]);

  // Reset timer when question changes
  useEffect(() => {
    setTimeRemaining(20);
    setIsAnswered(false);
    setSelectedAnswer(null);

    // Play game start sound for first question
    if (currentQuestionIndex === 0) {
      playSound.gameStart();
    }
  }, [currentQuestionIndex]);

  const currentQuestion = questions?.[currentQuestionIndex];

  const handleAnswerSelect = async (answerIndex: number) => {
    if (isAnswered || !currentQuestion) return;

    setSelectedAnswer(answerIndex);
    setIsAnswered(true);

    const timeSpent = 20 - timeRemaining;
    const isCorrect = answerIndex === currentQuestion.correctAnswer;

    const answerData = {
      questionId: currentQuestion.id,
      selectedAnswer: answerIndex,
      isCorrect,
      timeSpent,
    };

    setAnswers(prev => [...prev, answerData]);

    // Play sound based on answer correctness
    if (isCorrect) {
      playSound.correctAnswer();
    } else {
      playSound.wrongAnswer();
    }

    // Submit answer to backend
    try {
      const result = await submitAnswerMutation.mutateAsync(answerData);
      setScore(prev => prev + result.points);
    } catch (error) {
      console.error("Failed to submit answer:", error);
    }

    // Move to next question after delay
    setTimeout(() => {
      if (currentQuestionIndex < (questions?.length || 0) - 1) {
        setCurrentQuestionIndex(prev => prev + 1);
      } else {
        // Game complete - play completion sound
        playSound.gameComplete();
        completeGameMutation.mutate();
      }
    }, 2000);
  };

  if (isLoading || !questions) {
    return (
      <div className="min-h-screen trivia-background relative overflow-hidden flex items-center justify-center">
        <div className="absolute inset-0 bg-background/30"></div>
        <div className="text-center relative z-10">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">جارٍ تحميل الأسئلة...</p>
        </div>
      </div>
    );
  }

  const progressPercentage = ((20 - timeRemaining) / 20) * 100;

  // Dynamic timer bar colors based on time remaining
  const getTimerBarClass = () => {
    if (timeRemaining <= 5) {
      return "bg-gradient-to-r from-red-500 to-red-600 animate-pulse";
    } else if (timeRemaining <= 10) {
      return "bg-gradient-to-r from-yellow-500 to-orange-500";
    } else {
      return "bg-gradient-to-r from-green-500 to-blue-500";
    }
  };

  return (
    <div className="min-h-screen trivia-background relative overflow-hidden">
      {/* Subtle overlay for content readability */}
      <div className="absolute inset-0 bg-background/30"></div>
      <div className="container mx-auto px-4 py-8 max-w-md relative z-10">
        {/* Game Header */}
        <div className="flex justify-between items-center mb-6" data-testid="game-header">
          <div className="text-center">
            <div className="text-2xl font-bold text-primary" data-testid="current-score">
              {score.toLocaleString()}
            </div>
            <div className="text-sm text-muted-foreground">نقطة</div>
          </div>
          <div className="text-center">
            <div className="text-lg font-medium" data-testid="question-progress">
              {currentQuestionIndex + 1} / {questions.length}
            </div>
            <div className="text-sm text-muted-foreground">سؤال</div>
          </div>
        </div>

        {/* Enhanced Visual Timer */}
        <div className="mb-6" data-testid="timer-section">
          <div className="flex justify-between items-center mb-3">
            <span className="text-sm text-muted-foreground">الوقت المتبقي</span>
            <span 
              className={`text-xl font-bold transition-all duration-300 ${
                timeRemaining <= 5 ? 'text-red-400 animate-bounce' : 
                timeRemaining <= 10 ? 'text-yellow-400' : 'text-green-400'
              }`} 
              data-testid="time-remaining"
            >
              {timeRemaining}s
            </span>
          </div>

          {/* Progress Bar Container */}
          <div className="relative">
            <div className="w-full bg-gray-700/50 rounded-full h-4 shadow-inner">
              <div
                className={`h-4 rounded-full transition-all duration-300 ease-out ${getTimerBarClass()}`}
                style={{ width: `${Math.max(0, 100 - progressPercentage)}%` }}
                data-testid="timer-progress"
              >
                {/* Glowing effect for low time */}
                {timeRemaining <= 5 && (
                  <div className="absolute inset-0 rounded-full bg-red-400/30 blur-sm animate-pulse"></div>
                )}
              </div>
            </div>

            {/* Tick marks for visual reference */}
            <div className="absolute top-0 w-full h-4 flex justify-between items-center px-1">
              {[...Array(4)].map((_, i) => (
                <div 
                  key={i} 
                  className="w-0.5 h-2 bg-white/30 rounded-full"
                ></div>
              ))}
            </div>
          </div>

          {/* Warning message for low time */}
          {timeRemaining <= 5 && timeRemaining > 0 && (
            <div className="text-center mt-2">
              <span className="text-red-400 text-sm font-medium animate-pulse">
                ⏰ الوقت ينفد! 
              </span>
            </div>
          )}
        </div>

        {/* Question */}
        <Card className="border border-primary/30 shadow-lg shadow-primary/20 bg-gradient-to-br from-card to-primary/5 mb-6">
          <CardContent className="p-6">
            <h3 className="text-xl font-medium text-center mb-6" data-testid="question-text">
              {currentQuestion?.text}
            </h3>

            {/* Answer Options */}
            <div className="space-y-3">
              {currentQuestion?.options.map((option, index) => (
                <Button
                  key={index}
                  variant="secondary"
                  disabled={isAnswered}
                  onClick={() => {
                    playSound.buttonClick();
                    handleAnswerSelect(index);
                  }}
                  className={`w-full bg-gradient-to-r from-accent/10 to-primary/10 hover:from-primary hover:to-accent hover:text-primary-foreground border border-accent/30 rounded-lg p-4 text-right transition-all duration-300 transform hover:scale-[1.02] hover:-rotate-1 shadow-lg hover:shadow-accent/30 h-auto ${
                    isAnswered
                      ? selectedAnswer === index
                        ? index === currentQuestion.correctAnswer
                          ? "bg-green-600 text-white"
                          : "bg-red-600 text-white"
                        : index === currentQuestion.correctAnswer
                        ? "bg-green-600 text-white"
                        : ""
                      : ""
                  }`}
                  data-testid={`answer-option-${index}`}
                >
                  {option}
                </Button>
              ))}
            </div>
          </CardContent>
        </Card>

        {isAnswered && (
          <div className="text-center text-lg font-bold animate-bounce" data-testid="answer-feedback">
            {selectedAnswer === currentQuestion?.correctAnswer
              ? <span className="text-green-400">إجابة صحيحة! 🎉✨</span>
              : <span className="text-red-400">إجابة خاطئة 😔</span>}
          </div>
        )}
      </div>
    </div>
  );
}

